library(readxl)library(tidyverse)dat <- read_excel("~/Documents/Projects/TETC/Teacher Education Technology Competencies Survey_November 12, 2018_20.32.xlsx", skip=0)dat1<-dat %>%   slice(3:338)names(dat1)[22]<- "PK_12_Years"names(dat1)[23]<- "Teach_ED_Years"names(dat1)[24]<- "Position"dat1<-dat1%>%  mutate(PK_12_Years=as.integer(PK_12_Years),         Teach_ED_Years=as.integer(Teach_ED_Years),         Group_PK_12=ifelse(PK_12_Years <=6, 'little',                            ifelse(PK_12_Years >6 & PK_12_Years<=14, "some",                                   ifelse(PK_12_Years >14, "lots", PK_12_Years))))#count(Group_PK_12)#note: selected these values (6,14) based on trial and error to give best distribution of data#Anova of Group Differneces for PK_12_Years based on Years Teacher Ed (not Sig)model <- aov(dat1$Teach_ED_Years~dat1$Group_PK_12)summary (model) TukeyHSD(model)#Anova of Group Differneces for Teacher Ed based on PK_12_Years (not Sig but p=.07)dat1<-dat1%>%  mutate(PK_12_Years=as.integer(PK_12_Years),         Teach_ED_Years=as.integer(Teach_ED_Years),         Group_Teach_ED=ifelse(Teach_ED_Years <=6, 'little',                               ifelse(Teach_ED_Years >6 & Teach_ED_Years<=14, "some",                                      ifelse(Teach_ED_Years >14, "lots", Teach_ED_Years))))#count(Group_Teach_ED)model <- aov(dat1$PK_12_Years~dat1$Group_Teach_ED)summary (model) TukeyHSD(model)# group_by(Group_PK_12) %>% # summarize(Group_PK_12_mean = mean(Teach_ED_Years,na.rm=TRUE))#Anova of Group Differneces for Positions based on Years Teacher Ed (p<.001***)model <- aov(dat1$Teach_ED_Years~dat1$Position)summary (model) TukeyHSD(model)#a number of significant differences in groups (see output)#Anova of Group Differneces for Positions based on PK 12 Years (p<.001***)model <- aov(dat1$PK_12_Years~dat1$Position)summary (model) TukeyHSD(model)#all sig differences associated with "other" group (see output)#Correlation For PK_12 Years and Teacher Ed Years (p=.36, person coef=.05)cor.test(dat1$PK_12_Years, dat1$Teach_ED_Years, method = "pearson")#Graphslibrary(ggplot2)ggplot(data=dat1, aes(x=PK_12_Years))+geom_bar()ggplot(data=dat1, aes(x=Teach_ED_Years))+geom_bar()ggplot(data=dat1, aes(x=Position))+geom_bar()ggplot(data=dat1, aes(x=PK_12_Years, fill=Position))+geom_bar(position="fill")ggplot(data=dat1, aes(x=PK_12_Years, y=Teach_ED_Years))+geom_point()#calculate frequence table for Positiondat1 %>%  group_by(Position)%>%  summarize(freq=n())#calculate frequence table for Years PK-12 Teachingdat1 %>%  group_by(PK_12_Years)%>%  summarize(freq=n())#calculate frequence table for Years Teacher Educationdat1 %>%  group_by(Teach_ED_Years)%>%  summarize(freq=n())#plot looking at PK and Teach for each Position groupdat1 %>%   select(Teach_ED_Years, PK_12_Years, Position) %>%   gather(key,val,-Position) %>%   ggplot(aes(x=Position, y=val, fill=key))+geom_col(position="dodge")+coord_flip()#polar plot groups vs Years Teaching PK-12ggplot(data = dat1) +  geom_col(aes(x = Position, fill = Position, y = PK_12_Years),           width = 1) +  geom_hline(yintercept = seq(0, 40, by = 10),             color = "darkgrey",             size = 5) +  geom_vline(xintercept = seq(0, 6.5, by = 1),             color = "darkgrey",             size = .5) +  coord_polar() +  theme_minimal() +  labs(x = NULL, y = NULL) +  theme(axis.text.y = element_blank(),        legend.position = "none",        panel.grid = element_blank())#polar plot groups vs Years Teacher Edggplot(data = dat1) +  geom_col(aes(x = Position, fill = Position, y = Teach_ED_Years),           width = 1) +  geom_hline(yintercept = seq(0, 40, by = 10),             color = "darkgrey",             size = 5) +  geom_vline(xintercept = seq(0, 6.5, by = 1),             color = "darkgrey",             size = .5) +  coord_polar() +  theme_minimal() +  labs(x = NULL, y = NULL) +  theme(axis.text.y = element_blank(),        legend.position = "none",        panel.grid = element_blank())